        private List<string> GetKeyList(double value)
        {
            List<string> KeyList = new List<string>();
            double min = value - (value / 2);  //50 % less
                                               //double max = value + (value / 2);  //50 % More
            double inc = value / 10;             //10 % change
            for (int i = 1; i <= 11; i++)
            {
                KeyList.Add(min.ToString("0.##"));
                min = min + inc;
            }
            return KeyList;
        }

